#!/usr/bin/ruby
#

require_relative 'KthOf2SortedArray'

def check(nums1, nums2)
	res = (a=(nums1+nums2).sort).size%2==0 ? a[(a.size-1)/2,2] : a[a.size/2,1]
	sum = 0
	res.each {|x| sum+=x}
	return sum*1.0/res.size
end

def median(nums1, nums2)
	len = nums1.size + nums2.size
	sum = kthOf2Sorted nums1, nums2, (len-1)/2
	i = 1
	# puts "#{sum}"
	if len%2==0 then
		sum += kthOf2Sorted nums1, nums2, len/2
		i += 1
	end
	sum*1.0/i
end

